import 'react-native-gesture-handler';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as SplashScreen from 'expo-splash-screen';
import React, { useEffect, useCallback } from 'react';
import { LogBox, Platform, View, Text, AppState } from 'react-native';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { enableScreens } from 'react-native-screens';

// Import web polyfills first - ONLY for web
if (Platform.OS === 'web') {
    require('./utils/webPolyfills');
}

// Apply global patches
import ErrorBoundaryWrapper from './components/common/ErrorBoundaryWrapper';
import RootNavigator from './navigation/RootNavigator';
import optimizedApiClient from './services/api/client';
import { AppProvider } from './store/contexts/AppContext';
import { AuthProvider } from './store/contexts/AuthContext';
import { ThemeProvider } from './store/contexts/ThemeContext';
import { devLog, devError, devWarn } from './utils';
import { showErrorAlert } from './utils/alerts';
import appStability, { getPerformanceReport } from './utils/appStability';
import appStabilityManager from './utils/appStabilityEnhancements';
import { cache } from './utils/cacheManager';
import { loadFonts } from './utils/fontLoader';
import { applyGlobalPatches } from './utils/globalPatches';
import { onNetworkChange } from './utils/networkRetry';

applyGlobalPatches();

// Enable screens for better performance
enableScreens();

// Ignore specific warnings
LogBox.ignoreLogs([
    'Non-serializable values were found in the navigation state',
    'VirtualizedLists should never be nested',
    'Invariant Violation: "main" has not been registered',
    'Setting a timer for a long period of time',
    'Possible Unhandled Promise Rejection',
]);

// Keep splash screen visible while loading (native only)
if (Platform.OS !== 'web') {
    SplashScreen.preventAutoHideAsync().catch(() => {
        // Handle error silently
    });
}

// Global error handler
const globalErrorHandler = (error, isFatal) => {
    if (__DEV__) {
        devError('App', 'Global error', { error, isFatal });
    }

    // Log to error tracking service
    // Sentry.captureException(error);

    if (isFatal) {
        showErrorAlert(
            'Application Error',
            'The app encountered a critical error and needs to restart.',
            () => {
                // Restart app or navigate to safe screen
            },
        );
    }
};

// Set global error handlers
// eslint-disable-next-line no-undef
if (typeof ErrorUtils !== 'undefined') {
    // eslint-disable-next-line no-undef
    ErrorUtils.setGlobalHandler(globalErrorHandler);
}

export default function App() {
    const [appIsReady, setAppIsReady] = React.useState(false);
    const [initError, setInitError] = React.useState(null);
    const appStateRef = React.useRef(AppState.currentState);

    useEffect(() => {
        async function prepare() {
            try {
                // Set up auth failure handler
                optimizedApiClient.setAuthFailureHandler(() => {
                    // Handle auth failure globally
                    const { resetRoot } = require('./navigation/NavigationService');
                    resetRoot();
                });

                // Load fonts (will handle web gracefully)
                await loadFonts();

                // Set up API client authorization header if token exists
                const token = await AsyncStorage.getItem('userToken');
                if (token) {
                    optimizedApiClient.client.defaults.headers.common[
                        'Authorization'
                    ] = `Bearer ${token}`;
                }

                // Preload critical cache data
                await cache.preload([
                    {
                        key: 'app_config',
                        fetcher: async () => {
                            // Fetch app configuration
                            return { version: '1.0.0', features: {} };
                        },
                        options: { ttl: 24 * 60 * 60 * 1000, persistent: true },
                    },
                ]);

                // Initialize app stability monitoring
                appStabilityManager.initialize();

                // Add artificial delay for splash screen (native only)
                if (Platform.OS !== 'web') {
                    await new Promise((resolve) => setTimeout(resolve, 1000));
                }
            } catch (e) {
                devWarn('App', 'App initialization error', e);
                setInitError(e);
            } finally {
                setAppIsReady(true);
            }
        }

        prepare();

        // Monitor app state changes
        const subscription = AppState.addEventListener('change', (nextAppState) => {
            if (appStateRef.current.match(/inactive|background/) && nextAppState === 'active') {
                // App has come to the foreground
                devLog('App', 'App has come to the foreground!');

                // Refresh critical data
                if (global.refreshUserData) {
                    global.refreshUserData();
                }
            }
            appStateRef.current = nextAppState;
        });

        // Monitor network changes
        const unsubscribeNetwork = onNetworkChange((isOnline) => {
            devLog('App', `Network status: ${isOnline ? 'Online' : 'Offline'}`);

            // Set global network status
            global.isOnline = isOnline;

            // Show/hide offline banner
            if (global.setOfflineBanner) {
                global.setOfflineBanner(!isOnline);
            }
        });

        // Cleanup
        return () => {
            // Use the subscription's remove method instead of removeEventListener
            if (subscription && typeof subscription.remove === 'function') {
                subscription.remove();
            }
            unsubscribeNetwork();
            appStability.cleanup();
        };
    }, []);

    const onLayoutRootView = useCallback(async () => {
        if (appIsReady && Platform.OS !== 'web') {
            await SplashScreen.hideAsync().catch(() => {
                // Handle error silently
            });
        }
    }, [appIsReady]);

    // Performance monitoring in development
    useEffect(() => {
        if (__DEV__) {
            const interval = setInterval(() => {
                const report = getPerformanceReport();
                devLog('App', 'Performance Report', report);
            }, 60000); // Every minute

            return () => clearInterval(interval);
        }
    }, []);

    if (!appIsReady) {
        return null;
    }

    // Show initialization error screen
    if (initError) {
        return (
            <SafeAreaProvider>
                <View
                    style={{
                        flex: 1,
                        justifyContent: 'center',
                        alignItems: 'center',
                        padding: 20,
                    }}
                >
                    <Text style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 10 }}>
                        Initialization Error
                    </Text>
                    <Text style={{ textAlign: 'center', color: '#666' }}>
                        {initError.message || 'Failed to initialize the app'}
                    </Text>
                </View>
            </SafeAreaProvider>
        );
    }

    return (
        <ErrorBoundaryWrapper
            showDetails={__DEV__}
            onReset={() => {
                // Reset app state
                cache.clear();
                setAppIsReady(false);
                setInitError(null);
                // Re-initialize
                setTimeout(() => setAppIsReady(true), 100);
            }}
        >
            <SafeAreaProvider onLayout={onLayoutRootView}>
                <AppProvider>
                    <ThemeProvider>
                        <AuthProvider>
                            <RootNavigator />
                        </AuthProvider>
                    </ThemeProvider>
                </AppProvider>
            </SafeAreaProvider>
        </ErrorBoundaryWrapper>
    );
}

// frontend/src/components/common/CachedImage.js
import * as Crypto from 'expo-crypto';
import * as FileSystem from 'expo-file-system';
import React, { useState, useEffect } from 'react';
import { Image, View, ActivityIndicator } from 'react-native';

const IMAGE_CACHE_DIR = `${FileSystem.cacheDirectory}images/`;

export const CachedImage = ({ source, style, ...props }) => {
    const [imageUri, setImageUri] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadImage();
    }, [source.uri]);

    const loadImage = async () => {
        if (!source.uri) {
            setLoading(false);
            return;
        }

        try {
            // Create cache directory if it doesn't exist
            await FileSystem.makeDirectoryAsync(IMAGE_CACHE_DIR, {
                intermediates: true,
            });

            // Generate cache key
            const hash = await Crypto.digestStringAsync(
                Crypto.CryptoDigestAlgorithm.MD5,
                source.uri,
            );
            const cachedPath = `${IMAGE_CACHE_DIR}${hash}.jpg`;

            // Check if cached
            const info = await FileSystem.getInfoAsync(cachedPath);

            if (info.exists) {
                setImageUri(cachedPath);
            } else {
                // Download and cache
                await FileSystem.downloadAsync(source.uri, cachedPath);
                setImageUri(cachedPath);
            }
        } catch (error) {
            console.error('Image cache error:', error);
            setImageUri(source.uri); // Fallback to network image
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <View style={[style, { justifyContent: 'center', alignItems: 'center' }]}>
                <ActivityIndicator />
            </View>
        );
    }

    return <Image {...props} source={{ uri: imageUri }} style={style} />;
};

import React from 'react';
import { View, Text } from 'react-native';

import { styles } from '../../styles/components/common/EmptyStateStyles';

import Icon from './Icon.js';

const EmptyState = ({
    icon = 'inbox-outline',
    title = 'No data available',
    message = 'There is nothing to display at the moment.',
    iconSize = 64,
    iconColor,
    action,
    style,
    titleStyle,
    messageStyle,
}) => {
    return (
        <View style={[styles.container, style]}>
            <Icon
                name={icon}
                size={iconSize}
                color={iconColor || styles.icon.color}
                style={styles.icon}
            />
            <Text style={[styles.title, titleStyle]}>{title}</Text>
            <Text style={[styles.message, messageStyle]}>{message}</Text>
            {action && <View style={styles.actionContainer}>{action}</View>}
        </View>
    );
};

export default EmptyState;

import React, { Component } from 'react';
import { View, Text, TouchableOpacity, ScrollView } from 'react-native';

import { styles } from '../../styles/components/common/ErrorBoundaryStyles';

import Icon from './Icon.js';

class ErrorBoundary extends Component {
    constructor(props) {
        super(props);
        this.state = { hasError: false, error: null, errorInfo: null };
    }

    static getDerivedStateFromError(error) {
        return { hasError: true };
    }

    componentDidCatch(error, errorInfo) {
        // Log error to error reporting service
        console.error('Error caught by ErrorBoundary:', error, errorInfo);
        this.setState({
            error,
            errorInfo,
        });
    }

    resetError = () => {
        this.setState({ hasError: false, error: null, errorInfo: null });
    };

    render() {
        if (this.state.hasError) {
            return (
                <ScrollView style={styles.container} contentContainerStyle={styles.content}>
                    <Icon name="alert-circle-outline" size={80} color="#EF4444" />
                    <Text style={styles.title}>Oops! Something went wrong</Text>
                    <Text style={styles.message}>
                        We're sorry for the inconvenience. Please try restarting the app.
                    </Text>

                    <TouchableOpacity style={styles.button} onPress={this.resetError}>
                        <Text style={styles.buttonText}>Try Again</Text>
                    </TouchableOpacity>

                    {__DEV__ && (
                        <View style={styles.errorDetails}>
                            <Text style={styles.errorTitle}>Error Details (Dev Only):</Text>
                            <Text style={styles.errorText}>
                                {this.state.error && this.state.error.toString()}
                            </Text>
                            <Text style={styles.errorStack}>
                                {this.state.errorInfo && this.state.errorInfo.componentStack}
                            </Text>
                        </View>
                    )}
                </ScrollView>
            );
        }

        return this.props.children;
    }
}

export default ErrorBoundary;

// frontend/src/components/common/ErrorBoundaryWrapper.js
import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, Platform } from 'react-native';

import { colors, fonts, spacing } from '../../constants/theme';

import Icon from './Icon';


class ErrorBoundaryWrapper extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasError: false,
            error: null,
            errorInfo: null,
            errorCount: 0,
        };
    }

    static getDerivedStateFromError(error) {
        return { hasError: true };
    }

    async componentDidCatch(error, errorInfo) {
        // Log error to console in development
        if (__DEV__) {
            console.error('ErrorBoundary caught:', error);
            console.error('Error Info:', errorInfo);
        }

        // Track error count
        const errorCount = this.state.errorCount + 1;

        // Store error details
        this.setState({
            error,
            errorInfo,
            errorCount,
        });

        // Log to error tracking service (e.g., Sentry, Bugsnag)
        this.logErrorToService(error, errorInfo);

        // Store error in AsyncStorage for debugging
        try {
            const errorLog = {
                error: error.toString(),
                componentStack: errorInfo.componentStack,
                timestamp: new Date().toISOString(),
                platform: Platform.OS,
                version: Platform.Version,
            };

            await AsyncStorage.setItem(`error_log_${Date.now()}`, JSON.stringify(errorLog));
        } catch (storageError) {
            console.error('Failed to store error log:', storageError);
        }
    }

    logErrorToService = (error, errorInfo) => {
        // TODO: Integrate with error tracking service
        // Example: Sentry.captureException(error, { extra: errorInfo });
    };

    resetError = () => {
        this.setState({
            hasError: false,
            error: null,
            errorInfo: null,
        });

        // Call optional recovery callback
        if (this.props.onReset) {
            this.props.onReset();
        }
    };

    reloadApp = () => {
        // In production, you might want to reload the app
        if (!__DEV__) {
            // RNRestart.Restart(); // If using react-native-restart
        } else {
            this.resetError();
        }
    };

    render() {
        if (this.state.hasError) {
            const { fallback, showDetails = __DEV__ } = this.props;

            // Use custom fallback if provided
            if (fallback) {
                return fallback(this.state.error, this.resetError);
            }

            return (
                <View style={styles.container}>
                    <ScrollView
                        contentContainerStyle={styles.scrollContent}
                        showsVerticalScrollIndicator={false}
                    >
                        <View style={styles.content}>
                            <Icon
                                name="alert-circle-outline"
                                size={80}
                                color={colors.error}
                                style={styles.icon}
                            />

                            <Text style={styles.title}>Oops! Something went wrong</Text>

                            <Text style={styles.message}>
                                {this.state.errorCount > 2
                                    ? 'The app is experiencing repeated errors. Please restart the app.'
                                    : 'We\'re sorry for the inconvenience. The error has been logged and we\'ll fix it soon.'}
                            </Text>

                            <View style={styles.buttonContainer}>
                                <TouchableOpacity
                                    style={[styles.button, styles.primaryButton]}
                                    onPress={this.resetError}
                                >
                                    <Text style={styles.primaryButtonText}>Try Again</Text>
                                </TouchableOpacity>

                                {this.state.errorCount > 2 && (
                                    <TouchableOpacity
                                        style={[styles.button, styles.secondaryButton]}
                                        onPress={this.reloadApp}
                                    >
                                        <Text style={styles.secondaryButtonText}>Restart App</Text>
                                    </TouchableOpacity>
                                )}
                            </View>

                            {showDetails && this.state.error && (
                                <View style={styles.errorDetails}>
                                    <Text style={styles.errorTitle}>Error Details (Dev Only)</Text>

                                    <View style={styles.errorBox}>
                                        <Text style={styles.errorLabel}>Error:</Text>
                                        <Text style={styles.errorText}>
                                            {this.state.error.toString()}
                                        </Text>
                                    </View>

                                    {this.state.error.stack && (
                                        <View style={styles.errorBox}>
                                            <Text style={styles.errorLabel}>Stack Trace:</Text>
                                            <ScrollView
                                                style={styles.stackTrace}
                                                horizontal
                                                showsHorizontalScrollIndicator
                                            >
                                                <Text style={styles.errorStack}>
                                                    {this.state.error.stack}
                                                </Text>
                                            </ScrollView>
                                        </View>
                                    )}

                                    {this.state.errorInfo && (
                                        <View style={styles.errorBox}>
                                            <Text style={styles.errorLabel}>Component Stack:</Text>
                                            <ScrollView
                                                style={styles.stackTrace}
                                                horizontal
                                                showsHorizontalScrollIndicator
                                            >
                                                <Text style={styles.errorStack}>
                                                    {this.state.errorInfo.componentStack}
                                                </Text>
                                            </ScrollView>
                                        </View>
                                    )}
                                </View>
                            )}
                        </View>
                    </ScrollView>
                </View>
            );
        }

        return this.props.children;
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: colors.background,
    },
    scrollContent: {
        flexGrow: 1,
        justifyContent: 'center',
    },
    content: {
        padding: spacing.xl,
        alignItems: 'center',
    },
    icon: {
        marginBottom: spacing.xl,
    },
    title: {
        fontSize: fonts.sizes.xxl,
        fontWeight: 'bold',
        color: colors.text,
        textAlign: 'center',
        marginBottom: spacing.md,
    },
    message: {
        fontSize: fonts.sizes.md,
        color: colors.textSecondary,
        textAlign: 'center',
        marginBottom: spacing.xl,
        lineHeight: fonts.sizes.md * 1.5,
    },
    buttonContainer: {
        width: '100%',
        maxWidth: 300,
    },
    button: {
        paddingVertical: spacing.md,
        paddingHorizontal: spacing.xl,
        borderRadius: 8,
        marginBottom: spacing.md,
    },
    primaryButton: {
        backgroundColor: colors.primary,
    },
    primaryButtonText: {
        color: colors.textInverse,
        fontSize: fonts.sizes.md,
        fontWeight: '600',
        textAlign: 'center',
    },
    secondaryButton: {
        backgroundColor: colors.surface,
        borderWidth: 1,
        borderColor: colors.border,
    },
    secondaryButtonText: {
        color: colors.text,
        fontSize: fonts.sizes.md,
        fontWeight: '600',
        textAlign: 'center',
    },
    errorDetails: {
        width: '100%',
        marginTop: spacing.xl,
        padding: spacing.md,
        backgroundColor: colors.surface,
        borderRadius: 8,
        borderWidth: 1,
        borderColor: colors.border,
    },
    errorTitle: {
        fontSize: fonts.sizes.lg,
        fontWeight: 'bold',
        color: colors.error,
        marginBottom: spacing.md,
    },
    errorBox: {
        marginBottom: spacing.md,
    },
    errorLabel: {
        fontSize: fonts.sizes.sm,
        fontWeight: '600',
        color: colors.text,
        marginBottom: spacing.xs,
    },
    errorText: {
        fontSize: fonts.sizes.sm,
        color: colors.textSecondary,
        fontFamily: Platform.OS === 'ios' ? 'Courier' : 'monospace',
    },
    stackTrace: {
        maxHeight: 100,
    },
    errorStack: {
        fontSize: fonts.sizes.xs,
        color: colors.textSecondary,
        fontFamily: Platform.OS === 'ios' ? 'Courier' : 'monospace',
        lineHeight: fonts.sizes.xs * 1.5,
    },
});

// HOC for wrapping components with error boundary
export const withErrorBoundary = (Component, errorBoundaryProps = {}) => {
    const WrappedComponent = (props) => (
        <ErrorBoundaryWrapper {...errorBoundaryProps}>
            <Component {...props} />
        </ErrorBoundaryWrapper>
    );

    WrappedComponent.displayName = `withErrorBoundary(${
        Component.displayName || Component.name || 'Component'
    })`;

    return WrappedComponent;
};

export default ErrorBoundaryWrapper;

