const Icon = ({ name, size = 24, color = '#000', style, ...props }) => {
    // For web, always use emoji/text icons
    if (Platform.OS === 'web') {
        const iconChar = WEB_ICON_MAP[name] || WEB_ICON_MAP.default;

        return (
            <View
                style={[
                    {
                        width: size,
                        height: size,
                        justifyContent: 'center',
                        alignItems: 'center',
                    },
                    style,
                ]}
            >
                <Text
                    style={{
                        fontSize: size * 0.8,
                        color: color,
                        textAlign: 'center',
                        lineHeight: size,
                    }}
                    allowFontScaling={false}
                    {...props}
                >
                    {iconChar}
                </Text>
            </View>
        );
    }

    // Native platform code
    try {
        const MaterialCommunityIcons =
            require('react-native-vector-icons/MaterialCommunityIcons').default;
        return (
            <MaterialCommunityIcons
                name={name}
                size={size}
                color={color}
                style={style}
                {...props}
            />
        );
    } catch (error) {
        // Fallback for native if vector icons fail
        const iconChar = WEB_ICON_MAP[name] || WEB_ICON_MAP.default;
        return (
            <View
                style={[
                    { width: size, height: size, justifyContent: 'center', alignItems: 'center' },
                    style,
                ]}
            >
                <Text style={{ fontSize: size * 0.8, color, textAlign: 'center' }}>{iconChar}</Text>
            </View>
        );
    }
};

export default Icon;

