export const CustomModal = ({
    visible,
    onClose,
    title,
    children,
    showCloseButton = true,
    closeOnBackdrop = true,
    closeOnBackButton = true,
    scrollable = true,
    fullScreen = false,
    position = 'center', // 'center', 'bottom', 'top'
    showHandle = false, // for bottom sheet style
    animationType = 'slide', // 'slide', 'fade'
    style,
    contentStyle,
    headerStyle,
    titleStyle,
    bodyStyle,
}) => {
