import { useState, useEffect, useCallback, useRef } from 'react';
import chatService from '../services/chatService';
import socketService from '../services/socketService';
import { devLog, devError } from '../utils/devLog';

export const useChatRoom = (roomId, userId) => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [connectionError, setConnectionError] = useState(false);
    const [typingUsers, setTypingUsers] = useState([]);
    const messageCache = useRef(new Set());

    // Initialize chat room
    useEffect(() => {
        let isMounted = true;

        const initializeRoom = async () => {
            try {
                setConnectionError(false);

                // Ensure socket is connected
                if (!socketService.isConnected() && userId) {
                    try {
                        await socketService.connect(userId);
                        devLog('useChatRoom', 'Socket connected successfully');
                    } catch (error) {
                        devError('useChatRoom', 'Socket connection failed', error);
                        setConnectionError(true);
                    }
                }

                // Join room
                if (socketService.isConnected()) {
                    socketService.joinRoom(roomId);
                    devLog('useChatRoom', `Joined room: ${roomId}`);
                }

                // Load initial messages
                const initialMessages = await chatService.getRoomMessages(roomId);
                if (isMounted) {
                    setMessages(initialMessages || []);
                    // Cache message IDs
                    initialMessages?.forEach(msg => messageCache.current.add(msg._id));
                }
            } catch (error) {
                devError('useChatRoom', 'Failed to initialize room', error);
                if (isMounted) {
                    setConnectionError(true);
                }
            } finally {
                if (isMounted) {
                    setLoading(false);
                }
            }
        };

        initializeRoom();

        return () => {
            isMounted = false;
            if (socketService.isConnected()) {
                socketService.leaveRoom(roomId);
            }
        };
    }, [roomId, userId]);

    // Handle new messages
    const handleNewMessage = useCallback((data) => {
        devLog('useChatRoom', 'New message event:', data);

        // Extract message from different possible data structures
        const message = data.message || data;

        // Only process if it's for this room
        if (data.roomId === roomId || message.room === roomId || message.roomId === roomId) {
            // Prevent duplicates
            if (!messageCache.current.has(message._id)) {
                messageCache.current.add(message._id);
                setMessages(prev => [...prev, message]);
            }
        }
    }, [roomId]);

    // Handle typing indicators
    const handleUserTyping = useCallback(({ userId, isTyping, roomId: typingRoomId }) => {
        if (typingRoomId === roomId) {
            setTypingUsers(prev => {
                if (isTyping) {
                    return prev.includes(userId) ? prev : [...prev, userId];
                } else {
                    return prev.filter(id => id !== userId);
                }
            });
        }
    }, [roomId]);

    // Setup socket listeners
    useEffect(() => {
        if (!socketService.isConnected()) return;

        devLog('useChatRoom', 'Setting up socket listeners');

        socketService.on('new_message', handleNewMessage);
        socketService.on('user_typing', handleUserTyping);

        return () => {
            socketService.off('new_message', handleNewMessage);
            socketService.off('user_typing', handleUserTyping);
        };
    }, [handleNewMessage, handleUserTyping]);

    // Send message
    const sendMessage = useCallback(async (content) => {
        if (!content?.trim()) return;

        try {
            const sentMessage = await chatService.sendMessage(roomId, content.trim());
            devLog('useChatRoom', 'Message sent:', sentMessage);
            return sentMessage;
        } catch (error) {
            devError('useChatRoom', 'Failed to send message', error);
            throw error;
        }
    }, [roomId]);

    // Send typing indicator
    const sendTypingIndicator = useCallback((isTyping) => {
        if (socketService.isConnected()) {
            socketService.typing(roomId, isTyping);
        }
    }, [roomId]);

    return {
        messages,
        loading,
        connectionError,
        typingUsers,
        sendMessage,
        sendTypingIndicator,
        isConnected: socketService.isConnected()
    };
};

/**
 * Hook for managing chat socket events
 * Simplifies the socket event registration and cleanup
 */

import { useEffect, useCallback } from 'react';
import socketService from '../services/socketService';
import socketEventManager from '../utils/socketEventManager';
import { devLog, devError } from '../utils';

export const useChatSocketEvents = (roomId, handlers) => {
    const { onNewMessage, onUserTyping, onRoomUsers } = handlers;

    // Join room when connected
    useEffect(() => {
        if (!roomId) return;

        const joinRoom = () => {
            if (socketService.isConnected()) {
                socketService.joinRoom(roomId);
                devLog('ChatSocket', `Joined room: ${roomId}`);
            }
        };

        // Join immediately if connected
        joinRoom();

        // Also join when connection is established
        const handleConnect = () => joinRoom();
        socketService.on('authenticated', handleConnect);

        return () => {
            if (socketService.isConnected()) {
                socketService.leaveRoom(roomId);
                devLog('ChatSocket', `Left room: ${roomId}`);
            }
            socketService.off('authenticated', handleConnect);
        };
    }, [roomId]);

    // Handle new messages
    useEffect(() => {
        if (!onNewMessage || !roomId) return;

        // Register with socketEventManager (handles global events)
        const unsubscribeManager = socketEventManager.on('new_message', (data) => {
            devLog('ChatSocket', 'New message from event manager:', data);
            if (data.roomId === roomId) {
                onNewMessage(data);
            }
        });

        // Also register directly with socket (as backup)
        const handleDirectMessage = (data) => {
            devLog('ChatSocket', 'New message from direct socket:', data);
            if (data.roomId === roomId) {
                onNewMessage(data);
            }
        };

        socketService.on('new_message', handleDirectMessage);

        return () => {
            unsubscribeManager();
            socketService.off('new_message', handleDirectMessage);
        };
    }, [roomId, onNewMessage]);

    // Handle typing indicators
    useEffect(() => {
        if (!onUserTyping || !roomId) return;

        const handleTyping = (data) => {
            if (data.roomId === roomId) {
                onUserTyping(data);
            }
        };

        socketService.on('user_typing', handleTyping);

        return () => {
            socketService.off('user_typing', handleTyping);
        };
    }, [roomId, onUserTyping]);

    // Handle room users
    useEffect(() => {
        if (!onRoomUsers || !roomId) return;

        socketService.on('room_users', onRoomUsers);

        return () => {
            socketService.off('room_users', onRoomUsers);
        };
    }, [roomId, onRoomUsers]);

    // Typing helper
    const sendTyping = useCallback((isTyping) => {
        if (socketService.isConnected() && roomId) {
            socketService.typing(roomId, isTyping);
        }
    }, [roomId]);

    return { sendTyping };
};

import { useEffect, useCallback, useRef } from 'react';
import socketService from '../services/socketService';
import { devLog } from '../utils/devLog';

/**
 * Hook to manage socket event listeners with automatic cleanup
 * @param {Object} eventHandlers - Object mapping event names to handler functions
 * @param {Array} deps - Dependencies array for the effect
 */
export const useSocketEvents = (eventHandlers = {}, deps = []) => {
    const handlersRef = useRef(eventHandlers);

    // Update handlers ref when they change
    useEffect(() => {
        handlersRef.current = eventHandlers;
    }, [eventHandlers]);

    useEffect(() => {
        if (!socketService) {
            devLog('useSocketEvents', 'Socket service not available');
            return;
        }

        // Create stable handlers that reference the latest handlers
        const stableHandlers = {};

        Object.entries(eventHandlers).forEach(([event, handler]) => {
            stableHandlers[event] = (...args) => {
                const currentHandler = handlersRef.current[event];
                if (currentHandler) {
                    currentHandler(...args);
                }
            };
        });

        // Register all event listeners
        Object.entries(stableHandlers).forEach(([event, handler]) => {
            devLog('useSocketEvents', `Registering listener for: ${event}`);
            socketService.on(event, handler);
        });

        // Cleanup function
        return () => {
            Object.entries(stableHandlers).forEach(([event, handler]) => {
                devLog('useSocketEvents', `Removing listener for: ${event}`);
                socketService.off(event, handler);
            });
        };
    }, deps); // Only re-register if deps change

    // Return socket connection state
    const isConnected = socketService.isConnected();

    return { isConnected };
};

