import eventService from '../../services/eventService';
import { useAuth } from '../../store/contexts/AuthContext';
import { useTheme } from '../../store/contexts/ThemeContext';
import { createEventStyles } from '../../styles/screens/events/CreateEventScreenStyles';
import { showErrorAlert, showSuccessAlert } from '../../utils/alerts';
import { formatDate } from '../../utils/formatting';

const CreateEventScreen = ({ navigation }) => {
    const theme = useTheme();
    const styles = createEventStyles(theme);
    const { user } = useAuth();

    const [loading, setLoading] = useState(false);
    const [showDatePicker, setShowDatePicker] = useState(false);
    const [showTimePicker, setShowTimePicker] = useState(false);
    const [showCategoryModal, setShowCategoryModal] = useState(false);
    const [showAudienceModal, setShowAudienceModal] = useState(false);

    const [formData, setFormData] = useState({
        title: '',
        description: '',
        date: new Date(),
        time: new Date(),
        location: {
            name: '',
            address: '',
        },
        maxAttendees: '',
        category: 'meetup',
        targetAudience: 'all',
        tags: [],
        isPublic: true,
    });

    const [formErrors, setFormErrors] = useState({});
    const [tagInput, setTagInput] = useState('');

    const categories = [
        { value: 'networking', label: 'Networking', icon: 'account-group' },
        { value: 'workshop', label: 'Workshop', icon: 'school' },
        { value: 'social', label: 'Social', icon: 'party-popper' },
        { value: 'meetup', label: 'Meetup', icon: 'coffee' },
        { value: 'conference', label: 'Conference', icon: 'presentation' },
        { value: 'other', label: 'Other', icon: 'dots-horizontal' },
    ];

    const audiences = [
        { value: 'all', label: 'Everyone' },
        { value: 'freelancers', label: 'Freelancers' },
        { value: 'entrepreneurs', label: 'Entrepreneurs' },
        { value: 'both', label: 'Both' },
    ];

    const validateForm = () => {
        const errors = {};

        if (!formData.title.trim()) {
            errors.title = 'Title is required';
        }
        if (!formData.description.trim()) {
            errors.description = 'Description is required';
        }
        if (!formData.location.name.trim()) {
            errors.locationName = 'Location name is required';
        }

        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handleSubmit = async () => {
        if (!validateForm()) return;

        setLoading(true);
        try {
            // Format time from Date object to string
            const hours = formData.time.getHours();
            const minutes = formData.time.getMinutes();
            const timeString = `${hours.toString().padStart(2, '0')}:${minutes
                .toString()
                .padStart(2, '0')}`;

            const eventData = {
                ...formData,
                time: timeString,
                maxAttendees: formData.maxAttendees ? parseInt(formData.maxAttendees) : null,
            };

            await eventService.createEvent(eventData);
            showSuccessAlert('Success', 'Event created successfully!');
            navigation.goBack();
        } catch (error) {
            console.error('Failed to create event:', error);
            showErrorAlert('Error', error.message || 'Failed to create event');
        } finally {
            setLoading(false);
        }
    };

    const handleDateChange = (event, selectedDate) => {
        setShowDatePicker(false);
        if (selectedDate) {
            setFormData({ ...formData, date: selectedDate });
        }
    };

    const handleTimeChange = (event, selectedTime) => {
        setShowTimePicker(false);
        if (selectedTime) {
            setFormData({ ...formData, time: selectedTime });
        }
    };

    const formatTime = (date) => {
        const hours = date.getHours();
        const minutes = date.getMinutes();
        const ampm = hours >= 12 ? 'PM' : 'AM';
        const displayHours = hours % 12 || 12;
        return `${displayHours}:${minutes.toString().padStart(2, '0')} ${ampm}`;
    };

    const handleAddTag = () => {
        if (tagInput.trim() && !formData.tags.includes(tagInput.trim())) {
            setFormData({
                ...formData,
                tags: [...formData.tags, tagInput.trim().toLowerCase()],
            });
            setTagInput('');
        }
    };

    const handleRemoveTag = (tag) => {
        setFormData({
            ...formData,
            tags: formData.tags.filter((t) => t !== tag),
        });
    };

    const selectedCategory = categories.find((cat) => cat.value === formData.category);
    const selectedAudience = audiences.find((aud) => aud.value === formData.targetAudience);

    return (
        <SafeAreaView style={styles.safeArea}>
            <ScrollView
                style={styles.container}
                contentContainerStyle={styles.scrollContent}
                showsVerticalScrollIndicator={false}
            >
                <Text style={styles.sectionTitle}>Event Details</Text>

                <TextInput
                    label="Event Title"
                    value={formData.title}
                    onChangeText={(text) => setFormData({ ...formData, title: text })}
                    mode="outlined"
                    style={styles.input}
                    error={!!formErrors.title}
                    theme={{ colors: { primary: theme.colors.primary } }}
                />
                {formErrors.title && <Text style={styles.errorText}>{formErrors.title}</Text>}

                <TextInput
                    label="Description"
                    value={formData.description}
                    onChangeText={(text) => setFormData({ ...formData, description: text })}
                    mode="outlined"
                    multiline
                    numberOfLines={4}
                    style={styles.input}
                    error={!!formErrors.description}
                    theme={{ colors: { primary: theme.colors.primary } }}
                />
                {formErrors.description && (
                    <Text style={styles.errorText}>{formErrors.description}</Text>
                )}

                <Text style={styles.sectionTitle}>Date & Time</Text>

                <TouchableOpacity
                    style={styles.dateTimeSelector}
                    onPress={() => setShowDatePicker(true)}
                >
                    <Icon name="calendar" size={24} color={theme.colors.primary} />
                    <Text style={styles.dateTimeText}>{formatDate(formData.date)}</Text>
                </TouchableOpacity>

                <TouchableOpacity
                    style={styles.dateTimeSelector}
                    onPress={() => setShowTimePicker(true)}
                >
                    <Icon name="clock-outline" size={24} color={theme.colors.primary} />
                    <Text style={styles.dateTimeText}>{formatTime(formData.time)}</Text>
                </TouchableOpacity>

                <Text style={styles.sectionTitle}>Location</Text>

                <TextInput
                    label="Location Name"
                    value={formData.location.name}
                    onChangeText={(text) =>
                        setFormData({
                            ...formData,
                            location: { ...formData.location, name: text },
                        })
                    }
                    mode="outlined"
                    style={styles.input}
                    error={!!formErrors.locationName}
                    theme={{ colors: { primary: theme.colors.primary } }}
                />
                {formErrors.locationName && (
                    <Text style={styles.errorText}>{formErrors.locationName}</Text>
                )}

                <TextInput
                    label="Address (Optional)"
                    value={formData.location.address}
                    onChangeText={(text) =>
                        setFormData({
                            ...formData,
                            location: { ...formData.location, address: text },
                        })
                    }
                    mode="outlined"
                    style={styles.input}
                    theme={{ colors: { primary: theme.colors.primary } }}
                />

                <Text style={styles.sectionTitle}>Event Settings</Text>

                <TextInput
                    label="Max Attendees (Optional)"
                    value={formData.maxAttendees}
                    onChangeText={(text) => setFormData({ ...formData, maxAttendees: text })}
                    mode="outlined"
                    keyboardType="numeric"
                    style={styles.input}
                    theme={{ colors: { primary: theme.colors.primary } }}
                />

                {/* Category Selection */}
                <TouchableOpacity
                    style={styles.selectorButton}
                    onPress={() => setShowCategoryModal(true)}
                >
                    <View style={styles.selectorContent}>
                        <Icon name={selectedCategory.icon} size={24} color={theme.colors.primary} />
                        <View style={styles.selectorTextContainer}>
                            <Text style={styles.selectorLabel}>Category</Text>
                            <Text style={styles.selectorValue}>{selectedCategory.label}</Text>
                        </View>
                        <Icon name="chevron-down" size={24} color={theme.colors.textSecondary} />
                    </View>
                </TouchableOpacity>

                {/* Target Audience Selection */}
                <TouchableOpacity
                    style={styles.selectorButton}
                    onPress={() => setShowAudienceModal(true)}
                >
                    <View style={styles.selectorContent}>
                        <Icon name="account-group" size={24} color={theme.colors.primary} />
                        <View style={styles.selectorTextContainer}>
                            <Text style={styles.selectorLabel}>Target Audience</Text>
                            <Text style={styles.selectorValue}>{selectedAudience.label}</Text>
                        </View>
                        <Icon name="chevron-down" size={24} color={theme.colors.textSecondary} />
                    </View>
                </TouchableOpacity>

                <View style={styles.tagSection}>
                    <Text style={styles.radioLabel}>Tags</Text>
                    <View style={styles.tagInputContainer}>
                        <TextInput
                            placeholder="Add tags..."
                            value={tagInput}
                            onChangeText={setTagInput}
                            mode="outlined"
                            style={styles.tagInput}
                            theme={{ colors: { primary: theme.colors.primary } }}
                            onSubmitEditing={handleAddTag}
                        />
                        <Button
                            mode="contained"
                            onPress={handleAddTag}
                            style={styles.addTagButton}
                            disabled={!tagInput.trim()}
                        >
                            Add
                        </Button>
                    </View>
                    <View style={styles.tagsList}>
                        {formData.tags.map((tag, index) => (
                            <Chip
                                key={index}
                                style={styles.tag}
                                onClose={() => handleRemoveTag(tag)}
                            >
                                {tag}
                            </Chip>
                        ))}
                    </View>
                </View>

                <View style={styles.submitContainer}>
                    <Button
                        mode="outlined"
                        onPress={() => navigation.goBack()}
                        style={styles.cancelButton}
                        disabled={loading}
                    >
                        Cancel
                    </Button>
                    <Button
                        mode="contained"
                        onPress={handleSubmit}
                        style={styles.submitButton}
                        loading={loading}
                        disabled={loading}
                    >
                        Create Event
                    </Button>
                </View>
            </ScrollView>

            {showDatePicker && (
                <DateTimePicker
                    value={formData.date}
                    mode="date"
                    display={Platform.OS === 'ios' ? 'spinner' : 'default'}
                    onChange={handleDateChange}
                    minimumDate={new Date()}
                />
            )}

            {showTimePicker && (
                <DateTimePicker
                    value={formData.time}
                    mode="time"
                    display={Platform.OS === 'ios' ? 'spinner' : 'default'}
                    onChange={handleTimeChange}
                    is24Hour={false}
                />
            )}

            {/* Category Selection Modal */}
            <Portal>
                <Modal
                    visible={showCategoryModal}
                    onDismiss={() => setShowCategoryModal(false)}
                    contentContainerStyle={styles.modal}
                >
                    <Text style={styles.modalTitle}>Select Category</Text>
                    {categories.map((category) => (
                        <TouchableOpacity
                            key={category.value}
                            style={[
                                styles.modalOption,
                                formData.category === category.value && styles.modalOptionSelected,
                            ]}
                            onPress={() => {
                                setFormData({ ...formData, category: category.value });
                                setShowCategoryModal(false);
                            }}
                        >
                            <Icon
                                name={category.icon}
                                size={24}
                                color={
                                    formData.category === category.value
                                        ? theme.colors.primary
                                        : theme.colors.textSecondary
                                }
                            />
                            <Text
                                style={[
                                    styles.modalOptionText,
                                    formData.category === category.value &&
                                        styles.modalOptionTextSelected,
                                ]}
                            >
                                {category.label}
                            </Text>
                            {formData.category === category.value && (
                                <Icon name="check" size={24} color={theme.colors.primary} />
                            )}
                        </TouchableOpacity>
                    ))}
                </Modal>
            </Portal>

            {/* Audience Selection Modal */}
            <Portal>
                <Modal
                    visible={showAudienceModal}
                    onDismiss={() => setShowAudienceModal(false)}
                    contentContainerStyle={styles.modal}
                >
                    <Text style={styles.modalTitle}>Select Target Audience</Text>
                    {audiences.map((audience) => (
                        <TouchableOpacity
                            key={audience.value}
                            style={[
                                styles.modalOption,
                                formData.targetAudience === audience.value &&
                                    styles.modalOptionSelected,
                            ]}
                            onPress={() => {
                                setFormData({ ...formData, targetAudience: audience.value });
                                setShowAudienceModal(false);
                            }}
                        >
                            <Text
                                style={[
                                    styles.modalOptionText,
                                    formData.targetAudience === audience.value &&
                                        styles.modalOptionTextSelected,
                                ]}
                            >
                                {audience.label}
                            </Text>
                            {formData.targetAudience === audience.value && (
                                <Icon name="check" size={24} color={theme.colors.primary} />
                            )}
                        </TouchableOpacity>
                    ))}
                </Modal>
            </Portal>
        </SafeAreaView>
    );
};

export default React.memo(CreateEventScreen);

import { format } from 'date-fns';
import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, TouchableOpacity, SafeAreaView } from 'react-native';
import { Card, Button, Chip, Avatar, Menu, Divider } from 'react-native-paper';

import Icon from '../../components/common/Icon.js';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import eventService from '../../services/eventService';
import { useAuth } from '../../store/contexts/AuthContext';
import { useTheme } from '../../store/contexts/ThemeContext';
import { eventDetailStyles } from '../../styles/screens/events/EventDetailScreenStyles';
import { showErrorAlert, showSuccessAlert, showConfirmAlert } from '../../utils/alerts';

const EventDetailScreen = ({ route, navigation }) => {
    const theme = useTheme();
    const styles = eventDetailStyles(theme);
    const { user } = useAuth();
    const { eventId } = route.params;

    const [event, setEvent] = useState(null);
    const [loading, setLoading] = useState(true);
    const [actionLoading, setActionLoading] = useState(false);
    const [menuVisible, setMenuVisible] = useState(false);

    useEffect(() => {
        loadEventDetail();
    }, [eventId]);

    const loadEventDetail = async () => {
        try {
            const data = await eventService.getEvent(eventId);
            setEvent(data);
        } catch (error) {
            console.error('Failed to load event:', error);
            showErrorAlert('Error', 'Failed to load event details');
        } finally {
            setLoading(false);
        }
    };

    const handleJoinEvent = async () => {
        setActionLoading(true);
        try {
            await eventService.joinEvent(eventId);
            showSuccessAlert('Success', 'You have joined the event!');
            loadEventDetail();
        } catch (error) {
            showErrorAlert('Error', error.message || 'Failed to join event');
        } finally {
            setActionLoading(false);
        }
    };

    const handleLeaveEvent = async () => {
        setActionLoading(true);
        try {
            await eventService.leaveEvent(eventId);
            showSuccessAlert('Success', 'You have left the event');
            loadEventDetail();
        } catch (error) {
            showErrorAlert('Error', error.message || 'Failed to leave event');
        } finally {
            setActionLoading(false);
        }
    };

    const handleDeleteEvent = async () => {
        showConfirmAlert(
            'Delete Event',
            'Are you sure you want to delete this event? This action cannot be undone.',
            async () => {
                setActionLoading(true);
                try {
                    await eventService.deleteEvent(eventId);
                    showSuccessAlert('Success', 'Event deleted successfully');
                    navigation.goBack();
                } catch (error) {
                    showErrorAlert('Error', error.message || 'Failed to delete event');
                } finally {
                    setActionLoading(false);
                }
            },
        );
    };

    if (loading) {
        return <LoadingSpinner fullScreen text="Loading event..." />;
    }

    if (!event) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>Event not found</Text>
            </View>
        );
    }

    const isOrganizer = event.organizer?._id === user?._id;
    const isAttending = event.attendees?.some((att) => att._id === user?._id);
    const isFull = event.maxAttendees && event.attendees?.length >= event.maxAttendees;
    const eventDate = new Date(event.date);

    return (
        <SafeAreaView style={styles.safeArea}>
            <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
                <Card style={styles.headerCard}>
                    <Card.Content>
                        <View style={styles.dateContainer}>
                            <View style={styles.dateBadge}>
                                <Text style={styles.dateDay}>{format(eventDate, 'dd')}</Text>
                                <Text style={styles.dateMonth}>{format(eventDate, 'MMM')}</Text>
                            </View>
                            <View style={styles.headerInfo}>
                                <Text style={styles.title}>{event.title}</Text>
                                <View style={styles.metaContainer}>
                                    <Icon
                                        name="clock-outline"
                                        size={16}
                                        color={theme.colors.textSecondary}
                                    />
                                    <Text style={styles.metaText}>{event.time}</Text>
                                </View>
                            </View>
                            {isOrganizer && (
                                <Menu
                                    visible={menuVisible}
                                    onDismiss={() => setMenuVisible(false)}
                                    anchor={
                                        <TouchableOpacity
                                            onPress={() => setMenuVisible(true)}
                                            style={styles.menuButton}
                                        >
                                            <Icon
                                                name="dots-vertical"
                                                size={24}
                                                color={theme.colors.text}
                                            />
                                        </TouchableOpacity>
                                    }
                                >
                                    <Menu.Item
                                        onPress={() => {
                                            setMenuVisible(false);
                                            navigation.navigate('EditEvent', { eventId, event });
                                        }}
                                        title="Edit Event"
                                        leadingIcon="pencil"
                                    />
                                    <Divider />
                                    <Menu.Item
                                        onPress={() => {
                                            setMenuVisible(false);
                                            handleDeleteEvent();
                                        }}
                                        title="Delete Event"
                                        leadingIcon="delete"
                                        titleStyle={{ color: theme.colors.error }}
                                    />
                                </Menu>
                            )}
                        </View>
                    </Card.Content>
                </Card>

                <Card style={styles.infoCard}>
                    <Card.Content>
                        <Text style={styles.sectionTitle}>About this event</Text>
                        <Text style={styles.description}>{event.description}</Text>
                    </Card.Content>
                </Card>

                <Card style={styles.infoCard}>
                    <Card.Content>
                        <Text style={styles.sectionTitle}>Location</Text>
                        <View style={styles.locationContainer}>
                            <Icon name="map-marker" size={20} color={theme.colors.primary} />
                            <View style={styles.locationInfo}>
                                <Text style={styles.locationName}>{event.location.name}</Text>
                                {event.location.address && (
                                    <Text style={styles.locationAddress}>
                                        {event.location.address}
                                    </Text>
                                )}
                            </View>
                        </View>
                    </Card.Content>
                </Card>

                <Card style={styles.infoCard}>
                    <Card.Content>
                        <Text style={styles.sectionTitle}>
                            Attendees ({event.attendees?.length || 0}
                            {event.maxAttendees && `/${event.maxAttendees}`})
                        </Text>
                        <View style={styles.attendeesList}>
                            {event.attendees?.slice(0, 5).map((attendee, index) => (
                                <View key={attendee._id} style={styles.attendeeChip}>
                                    <Avatar.Text size={32} label={attendee.name.charAt(0)} />
                                    <Text style={styles.attendeeName}>{attendee.name}</Text>
                                </View>
                            ))}
                            {event.attendees?.length > 5 && (
                                <Text style={styles.moreAttendees}>
                                    +{event.attendees.length - 5} more
                                </Text>
                            )}
                        </View>
                    </Card.Content>
                </Card>

                <View style={styles.actionContainer}>
                    {isOrganizer ? (
                        <Button mode="contained" style={styles.actionButton} disabled>
                            You're the organizer
                        </Button>
                    ) : isAttending ? (
                        <Button
                            mode="outlined"
                            style={styles.actionButton}
                            onPress={handleLeaveEvent}
                            loading={actionLoading}
                            disabled={actionLoading}
                        >
                            Leave Event
                        </Button>
                    ) : (
                        <Button
                            mode="contained"
                            style={styles.actionButton}
                            onPress={handleJoinEvent}
                            loading={actionLoading}
                            disabled={actionLoading || isFull}
                        >
                            {isFull ? 'Event Full' : 'Join Event'}
                        </Button>
                    )}
                </View>
            </ScrollView>
        </SafeAreaView>
    );
};

export default React.memo(EventDetailScreen);

