                >
                    <Icon name="account-circle-outline" size={24} color={colors.textInverse} />
                </TouchableOpacity>
            ),
        });

        loadMessages();
        setupSocketListeners();

        return () => {
            cleanupSocketListeners();
            if (typingTimeoutRef.current) {
                clearTimeout(typingTimeoutRef.current);
            }
        };
    }, [userId]);

    const loadMessages = async () => {
        try {
            const data = await personalChatService.getMessages(userId);
            setMessages(data.reverse());

            // Mark messages as read
            if (conversationId) {
                await personalChatService.markAsRead(conversationId);
            }
        } catch (error) {
            console.error('Failed to load messages:', error);
        } finally {
            setLoading(false);
        }
    };

    const setupSocketListeners = () => {
        // Listen for new messages from this user
        socketService.on('private_message', handleNewMessage);
        socketService.on('user_typing', handleUserTyping);
        socketService.on('message_read', handleMessageRead);
    };

    const cleanupSocketListeners = () => {
        socketService.off('private_message', handleNewMessage);
        socketService.off('user_typing', handleUserTyping);
        socketService.off('message_read', handleMessageRead);
    };

    const handleNewMessage = useCallback(
        (message) => {
            if (message.sender === userId || message.recipient === userId) {
                setMessages((prev) => [...prev, message]);
                setTimeout(() => {
                    flatListRef.current?.scrollToEnd({ animated: true });
                }, 100);
            }
        },
        [userId],
    );

    const handleUserTyping = useCallback(
        ({ userId: typingUserId, isTyping: typing }) => {
            if (typingUserId === userId) {
                setIsTyping(typing);
            }
        },
        [userId],
    );

    const handleMessageRead = useCallback(({ messageIds }) => {
        setMessages((prev) =>
            prev.map((msg) => (messageIds.includes(msg._id) ? { ...msg, read: true } : msg)),
        );
    }, []);

    const sendMessage = async () => {
        if (!inputText.trim() || sending) return;

        const messageText = inputText.trim();
        setInputText('');
        setSending(true);

        try {
            await personalChatService.sendMessage(userId, messageText);

            // Optimistically add message to UI
            const optimisticMessage = {
                _id: `temp-${Date.now()}`,
                content: messageText,
                sender: user._id,
                recipient: userId,
                createdAt: new Date().toISOString(),
                read: false,
            };

            setMessages((prev) => [...prev, optimisticMessage]);

            setTimeout(() => {
                flatListRef.current?.scrollToEnd({ animated: true });
            }, 100);
        } catch (error) {
            console.error('Failed to send message:', error);
            showErrorAlert('Error', 'Failed to send message');
            setInputText(messageText);
        } finally {
            setSending(false);
        }
    };

    const handleTyping = (text) => {
        setInputText(text);

        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }

        if (text.trim() && socketService.isConnected()) {
            socketService.emit('typing', { recipientId: userId, isTyping: true });

            typingTimeoutRef.current = setTimeout(() => {
                socketService.emit('typing', { recipientId: userId, isTyping: false });
            }, 3000);
        } else if (socketService.isConnected()) {
            socketService.emit('typing', { recipientId: userId, isTyping: false });
        }
    };

    const renderMessage = ({ item, index }) => {
        const isOwnMessage = item.sender === user._id;
        const showTimestamp =
            index === 0 ||
            new Date(item.createdAt) - new Date(messages[index - 1]?.createdAt) > 300000;

        return (
            <>
                {showTimestamp && (
                    <View style={styles.timestampContainer}>
                        <Text style={styles.timestamp}>
                            {format(new Date(item.createdAt), 'HH:mm')}
                        </Text>
                    </View>
                )}

                <View style={[styles.messageContainer, isOwnMessage && styles.ownMessageContainer]}>
                    <View style={[styles.messageBubble, isOwnMessage && styles.ownMessageBubble]}>
                        <Text style={[styles.messageText, isOwnMessage && styles.ownMessageText]}>
                            {item.content}
                        </Text>

                        {isOwnMessage && (
                            <View style={styles.messageStatus}>
                                <Icon
                                    name={item.read ? 'check-all' : 'check'}
                                    size={16}
                                    color={item.read ? '#60A5FA' : '#FFFFFF99'}
                                />
                            </View>
                        )}
                    </View>
                </View>
            </>
        );
    };

    if (loading) {
        return <LoadingSpinner fullScreen text="Loading messages..." />;
    }

    return (
        <SafeAreaView style={styles.container}>
            <KeyboardAvoidingView
                style={styles.container}
                behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
                keyboardVerticalOffset={Platform.OS === 'ios' ? 90 : 0}
            >
                <FlatList
                    ref={flatListRef}
                    data={messages}
                    renderItem={renderMessage}
                    keyExtractor={(item) => item._id}
                    contentContainerStyle={styles.messagesList}
                    showsVerticalScrollIndicator={false}
                    onContentSizeChange={() =>
                        flatListRef.current?.scrollToEnd({ animated: false })
                    }
                    ListFooterComponent={
                        isTyping ? (
                            <View style={styles.typingIndicator}>
                                <Text style={styles.typingText}>{userName} is typing...</Text>
                            </View>
                        ) : null
                    }
                />

                <View style={styles.inputContainer}>
                    <View style={styles.inputWrapper}>
                        <TextInput
                            style={styles.input}
                            value={inputText}
                            onChangeText={handleTyping}
                            placeholder="Type a message"
                            placeholderTextColor={colors.textSecondary}
                            multiline
                            maxLength={1000}
                            onSubmitEditing={sendMessage}
                            blurOnSubmit={false}
                        />

                        <TouchableOpacity
                            onPress={sendMessage}
                            disabled={!inputText.trim() || sending}
                            style={[
                                styles.sendButton,
                                (!inputText.trim() || sending) && styles.sendButtonDisabled,
                            ]}
                        >
                            <Icon
                                name="send"
                                size={24}
                                color={
                                    inputText.trim() && !sending ? colors.primary : colors.disabled
                                }
                            />
                        </TouchableOpacity>
                    </View>
                </View>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );
};

