// frontend/src/services/authService.js
import AsyncStorage from '@react-native-async-storage/async-storage';

import apiClient from './api/client';
import { API_ENDPOINTS } from './api/endpoints';
import { devLog, devError } from '../utils';

const authService = {
    login: async (email, password) => {
        try {
            const response = await apiClient.post(API_ENDPOINTS.AUTH.LOGIN, {
                email,
                password,
            });

            // Handle both response.data and direct response formats
            const data = response.data || response;

            // Validate response structure
            if (!data || (!data.token && !data.accessToken)) {
                devError('AuthService', 'Invalid login response', data);
                throw new Error('Invalid response format from server');
            }

            // Normalize token field (handle both 'token' and 'accessToken')
            const token = data.token || data.accessToken;
            const user = data.user || data;

            return {
                token,
                user,
                ...data,
            };
        } catch (error) {
            devError('AuthService', 'Login service error', error);
            throw error;
        }
    },

    register: async (name, email, password, professionalPath = null) => {
        try {
            const payload = {
                name,
                email,
                password,
            };

            if (professionalPath) {
                payload.professionalPath = professionalPath;
            }

            devLog('AuthService', 'Register payload', payload);

            const response = await apiClient.post(API_ENDPOINTS.AUTH.REGISTER, payload);

            // The interceptor now returns response.data directly
            const data = response;

            devLog('AuthService', 'Register response data', data);

            // Validate response structure
            if (!data) {
                throw new Error('No response data from server');
            }

            // Backend might return user data directly without token for registration
            // Check multiple possible response formats

            // Format 1: { token, user }
            if (data.token && data.user) {
                return {
                    token: data.token,
                    user: data.user,
                };
            }

            // Format 2: { accessToken, user }
            if (data.accessToken && data.user) {
                return {
                    token: data.accessToken,
                    user: data.user,
                };
            }

            // Format 3: User object directly (need to login separately)
            if (data._id && data.email) {
                devLog('AuthService', 'Registration successful, but no token returned. User needs to login.');
                // Return user data without token - the auth context should handle this
                return {
                    token: null,
                    user: data,
                    requiresLogin: true,
                };
            }

            // Format 4: { success: true, data: { user } }
            if (data.success && data.data) {
                return {
                    token: data.data.token || null,
                    user: data.data.user || data.data,
                    requiresLogin: !data.data.token,
                };
            }

            // If we get here, the response format is unexpected
            devError('AuthService', 'Unexpected register response format', data);
            throw new Error(
                'Registration may have succeeded, but the response format was unexpected. Please try logging in.',
            );
        } catch (error) {
            devError('AuthService', 'Register service error', error);

            // The error has already been processed by the interceptor
            throw error;
        }
    },

    getToken: async () => {
        try {
            return await AsyncStorage.getItem('userToken');
        } catch (error) {
            devError('AuthService', 'Error getting token', error);
            return null;
        }
    },

    getCurrentUser: async () => {
        try {
            const response = await apiClient.get(API_ENDPOINTS.AUTH.ME);
            const data = response.data || response;

            if (!data || !data._id) {
                throw new Error('Invalid user data from server');
            }

            return data;
        } catch (error) {
            devError('AuthService', 'Get current user error', error);
            throw error;
        }
    },

    getMe: async () => {
        return authService.getCurrentUser();
    },

    updateOnboarding: async (professionalPath, pinnedModules) => {
        try {
            const response = await apiClient.put(API_ENDPOINTS.AUTH.UPDATE_ONBOARDING, {
                professionalPath,
                pinnedModules,
            });
            return response.data || response;
        } catch (error) {
            devError('AuthService', 'Update onboarding error', error);
            throw error;
        }
    },

    updateOnboardingPath: async (professionalPath) => {
        try {
            const response = await apiClient.put('/api/users/professional-path', {
                professionalPath,
            });
            return response.data || response;
        } catch (error) {
            devError('AuthService', 'Update onboarding path error', error);
            throw error;
        }
    },

    completeOnboarding: async (checklistItems) => {
        try {
            const response = await apiClient.put('/api/users/complete-onboarding', {
                selectedChecklistItems: checklistItems,
            });
            return response.data || response;
        } catch (error) {
            devError('AuthService', 'Complete onboarding error', error);
            throw error;
        }
    },

    updateProfile: async (profileData) => {
        try {
            const response = await apiClient.put(API_ENDPOINTS.AUTH.UPDATE_PROFILE, profileData);
            return response.data || response;
        } catch (error) {
            devError('AuthService', 'Update profile error', error);
            throw error;
        }
    },

    changePassword: async (currentPassword, newPassword) => {
        try {
            const response = await apiClient.put(API_ENDPOINTS.AUTH.CHANGE_PASSWORD, {
                currentPassword,
                newPassword,
            });
            return response.data || response;
        } catch (error) {
            devError('AuthService', 'Change password error', error);
            throw error;
        }
    },

    logout: async () => {
        try {
            await apiClient.post(API_ENDPOINTS.AUTH.LOGOUT);
        } catch (error) {
            devError('AuthService', 'Logout API error', error);
            // Don't throw, continue with local logout
        }
    },

    setAuthToken: (token) => {
        if (token) {
            apiClient.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        } else {
            delete apiClient.defaults.headers.common['Authorization'];
        }
    },
};

export default authService;

import apiClient from './api/client';
import { API_ENDPOINTS } from './api/endpoints';

// Cache for categories to avoid repeated API calls
const categoriesCache = new Map(); // professionalPath -> categories
const cacheTimestamp = new Map(); // professionalPath -> timestamp
const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

// Cache for budget entries
let entriesCache = null;
let entriesCacheTimestamp = null;

const budgetService = {
    getBudgetEntries: async (filters = {}) => {
        try {
            const response = await apiClient.get(API_ENDPOINTS.BUDGET.LIST, {
                params: filters,
            });
            // Response is now the data directly from interceptor
            return Array.isArray(response) ? response : response?.entries || [];
        } catch (error) {
            console.error('Error fetching budget entries:', error);
            throw error;
        }
    },

    createBudgetEntry: async (entry) => {
        try {
            const response = await apiClient.post(API_ENDPOINTS.BUDGET.CREATE, entry);
            return response;
        } catch (error) {
            console.error('Error creating budget entry:', error);
            throw error;
        }
    },

    updateBudgetEntry: async (id, entry) => {
        try {
            const response = await apiClient.put(API_ENDPOINTS.BUDGET.UPDATE(id), entry);
            return response;
        } catch (error) {
            console.error('Error updating budget entry:', error);
            throw error;
        }
    },

    deleteBudgetEntry: async (id) => {
        try {
            const response = await apiClient.delete(API_ENDPOINTS.BUDGET.DELETE(id));
            return response;
        } catch (error) {
            console.error('Error deleting budget entry:', error);
            throw error;
        }
    },

    getBudgetSummary: async (period = 'month') => {
        try {
            const response = await apiClient.get(API_ENDPOINTS.BUDGET.SUMMARY, {
                params: { period },
            });
            return response;
        } catch (error) {
            console.error('Error fetching budget summary:', error);
            throw error;
        }
    },

    /**
     * Fetches budget categories with caching
     */
    getCategories: async (professionalPath) => {
        const cacheKey = professionalPath || 'default';

        // Check if we have valid cached data
        if (
            categoriesCache.has(cacheKey) &&
            cacheTimestamp.has(cacheKey) &&
            Date.now() - cacheTimestamp.get(cacheKey) < CACHE_DURATION
        ) {
            return categoriesCache.get(cacheKey);
        }

        try {
            const response = await apiClient.get('/config/categories', {
                params: professionalPath ? { professionalPath } : {},
            });

            // Update cache
            categoriesCache.set(cacheKey, response);
            cacheTimestamp.set(cacheKey, Date.now());

            return response;
        } catch (error) {
            console.error('Failed to fetch categories from API:', error);

            // Return cached data if available, even if expired
            if (categoriesCache.has(cacheKey)) {
                return categoriesCache.get(cacheKey);
            }

            // Return default categories as fallback
            const defaultCategories =
                professionalPath === 'ENTREPRENEUR'
                    ? {
                        income: [
                            'Product Sales',
                            'Service Revenue',
                            'Investor Funding',
                            'Grants',
                            'Other Income',
                        ],
                        expense: [
                            'Salaries & Payroll',
                            'Office Rent',
                            'Legal & Accounting',
                            'Marketing & Sales',
                            'R&D',
                            'Operations',
                            'Other Expenses',
                        ],
                    }
                    : {
                        income: [
                            'Project-Based Income',
                            'Recurring Clients',
                            'Passive Income',
                            'Other Income',
                        ],
                        expense: [
                            'Cuota de AutÃ³nomo',
                            'Office/Coworking',
                            'Software & Tools',
                            'Professional Services',
                            'Marketing',
                            'Travel & Transport',
                            'Other Expenses',
                        ],
                    };

            return defaultCategories;
        }
    },

    // Clear categories cache when user changes professional path
    clearCategoriesCache: () => {
        categoriesCache.clear();
        cacheTimestamp.clear();
        entriesCache = null;
        entriesCacheTimestamp = null;
    },

    exportBudgetData: async (format = 'csv', dateRange) => {
        try {
            const response = await apiClient.get(API_ENDPOINTS.BUDGET.EXPORT, {
                params: { format, ...dateRange },
                responseType: 'blob',
            });
            return response;
        } catch (error) {
            console.error('Error exporting budget data:', error);
            throw error;
        }
    },
};

export default budgetService;

import { USE_MOCK, devLog } from '../config/development';
import { devWarn, devError } from '../utils/devLog';

import apiClient from './api/client';
import mockChatService from './mockChatService';
import socketService from './socketService';

const chatService = {
    // Initialize chat connection
    async initializeChat(userId) {
        if (USE_MOCK) {
            devLog('Chat', 'Using mock chat service');
            return Promise.resolve();
        }

        try {
            // Try to connect socket
            await socketService.connect(userId);
            return true;
        } catch (error) {
            console.error('Failed to initialize chat:', error);
            // Continue without socket - API fallback will be used
            return false;
        }
    },

    // Get room messages with fallback
    async getRoomMessages(roomId, options = {}) {
        if (USE_MOCK) {
            return mockChatService.getRoomMessages(roomId);
        }

        try {
            const params = {
                limit: options.limit || 50,
                ...(options.before && { before: options.before }),
            };

            // Fixed: Use correct endpoint structure
            const response = await apiClient.get(`/chat/rooms/${roomId}/messages`, { params });

            // Handle different response structures from backend
            let messages = [];

            // If backend returns wrapped response: { data: { messages: [...] } }
            if (response && response.data && Array.isArray(response.data.messages)) {
                devLog('ChatService', `Retrieved ${response.data.messages.length} messages from response.data.messages`);
                messages = response.data.messages;
            }
            // If backend returns: { messages: [...] }
            else if (response && Array.isArray(response.messages)) {
                devLog('ChatService', `Retrieved ${response.messages.length} messages from response.messages`);
                messages = response.messages;
            }
            // If backend returns array directly
            else if (Array.isArray(response)) {
                devLog('ChatService', `Retrieved ${response.length} messages (array response)`);
                messages = response;
            }
            // Fallback: check if response itself looks like a message array
            else if (response && response.length !== undefined) {
                devLog('ChatService', 'Converting array-like response to array');
                messages = Array.from(response);
            }

            // Validate messages have required structure
            const validMessages = messages.filter(
                (msg) => msg && msg._id && msg.sender && msg.content !== undefined,
            );

            if (validMessages.length !== messages.length) {
                devWarn(
                    'ChatService',
                    `Filtered out ${messages.length - validMessages.length} invalid messages`
                );
            }

            return validMessages;
        } catch (error) {
            devError('ChatService', 'Error fetching messages', error);

            // Fallback to mock data in development
            if (__DEV__) {
                devLog('ChatService', 'Using mock messages due to API error');
                return mockChatService.getRoomMessages(roomId);
            }

            return [];
        }
    },

    // Send message with fallback
    async sendMessage(roomId, content, type = 'text', attachments = []) {
        devLog('ChatService', 'ðŸ“¤ Sending message', { roomId, content, type });

        // Always use API to ensure message is saved
        // Socket will handle real-time delivery
        try {
            const response = await apiClient.post(`/chat/rooms/${roomId}/messages`, {
                content,
                type,
                attachments,
            });

            devLog('ChatService', 'âœ… Message sent via API', response);

            // The backend should emit the message via socket
            // so we just return the response
            return response;
        } catch (error) {
            devError('ChatService', 'Error sending message', error);
            throw error;
        }
    },

    // Get chat rooms
    async getChatRooms() {
        if (USE_MOCK) {
            return mockChatService.getRooms();
        }

        try {
            const response = await apiClient.get('/chat/rooms');
            return response || [];
        } catch (error) {
            console.error('Error fetching chat rooms:', error);

            // Fallback to mock data in development
            if (__DEV__) {
                console.log('Using mock rooms due to API error');
                return mockChatService.getRooms();
            }

            return [];
        }
    },

    // Join room with error handling
    async joinRoom(roomId) {
        if (socketService.isConnected()) {
            socketService.joinRoom(roomId);
        }

        // Always return messages regardless of socket status
        return this.getRoomMessages(roomId);
    },

    // Leave room
    leaveRoom(roomId) {
        if (socketService.isConnected()) {
            socketService.leaveRoom(roomId);
        }
    },

    // Check connection status
    isConnected() {
        return socketService.isConnected();
    },

    // Cleanup
    disconnect() {
        socketService.disconnect();
    },
};

export default chatService;

import apiClient from './api/client';
import { API_ENDPOINTS } from './api/endpoints';

const eventService = {
    getEvents: async (params = {}) => {
        try {
            // Convert the response to handle both array and object responses
            const response = await apiClient.get('/events', { params });

            // If response has events property, return that, otherwise assume it's an array
            if (response && response.events) {
                return response.events;
            } else if (Array.isArray(response)) {
                return response;
            } else {
                console.error('Unexpected response format:', response);
                return [];
            }
        } catch (error) {
            console.error('Error fetching events:', error);
            throw error;
        }
    },

    getEvent: async (id) => {
        try {
            const response = await apiClient.get(`/events/${id}`);
            return response;
        } catch (error) {
            console.error('Error fetching event:', error);
            throw error;
        }
    },

    createEvent: async (eventData) => {
        try {
            // Ensure required fields are properly formatted
            const formattedData = {
                ...eventData,
                date: new Date(eventData.date).toISOString(),
                location: {
                    name: eventData.location.name,
                    address: eventData.location.address || '',
                    city: eventData.location.city || 'Alicante',
                },
                tags: eventData.tags || [],
                maxAttendees: eventData.maxAttendees ? parseInt(eventData.maxAttendees) : null,
            };

            const response = await apiClient.post('/events', formattedData);
            return response;
        } catch (error) {
            console.error('Error creating event:', error);
            // Provide more specific error messages
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    updateEvent: async (id, eventData) => {
        try {
            const response = await apiClient.put(`/events/${id}`, eventData);
            return response;
        } catch (error) {
            console.error('Error updating event:', error);
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    deleteEvent: async (id) => {
        try {
            const response = await apiClient.delete(`/events/${id}`);
            return response;
        } catch (error) {
            console.error('Error deleting event:', error);
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    joinEvent: async (id) => {
        try {
            const response = await apiClient.post(`/events/${id}/join`);
            return response;
        } catch (error) {
            console.error('Error joining event:', error);
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    leaveEvent: async (id) => {
        try {
            const response = await apiClient.post(`/events/${id}/leave`);
            return response;
        } catch (error) {
            console.error('Error leaving event:', error);
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    cancelEvent: async (id) => {
        try {
            const response = await apiClient.post(`/events/${id}/cancel`);
            return response;
        } catch (error) {
            console.error('Error cancelling event:', error);
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    },

    getUpcomingEvents: async (limit = 5) => {
        try {
            const response = await apiClient.get('/events', {
                params: {
                    upcoming: 'true',
                    limit,
                },
            });

            if (response && response.events) {
                return response.events;
            } else if (Array.isArray(response)) {
                return response;
            } else {
                return [];
            }
        } catch (error) {
            console.error('Error fetching upcoming events:', error);
            return [];
        }
    },
};

