export default socketService;

/**
 * Fixed Socket Service
 * Handles WebSocket connections with proper error handling and fallback
 */

import AsyncStorage from '@react-native-async-storage/async-storage';
import { Platform } from 'react-native';
import io from 'socket.io-client';

import { API_BASE_URL } from '../config/network';
import { devLog, devError } from '../utils';

class SocketServiceFixed {
    constructor() {
        this.socket = null;
        this.isAuthenticated = false;
        this.userId = null;
        this.connectionState = 'disconnected';
        this.listeners = new Map();
    }

    async connect(userId) {
        if (this.socket?.connected) {
            devLog('Socket', 'Already connected');
            return Promise.resolve();
        }

        this.userId = userId;

        try {
            const token = await AsyncStorage.getItem('userToken');

            // Get socket URL
            let socketUrl = API_BASE_URL.replace('/api', '');

            // Fix localhost issues
            if (socketUrl.includes('localhost')) {
                if (Platform.OS === 'android') {
                    socketUrl = socketUrl.replace('localhost', '10.0.2.2');
                } else if (Platform.OS === 'ios' && !__DEV__) {
                    // In production iOS, use actual IP
                    socketUrl = socketUrl.replace('localhost', '127.0.0.1');
                }
            }

            devLog('Socket', `Connecting to: ${socketUrl}`);

            // Create socket with simplified options
            this.socket = io(socketUrl, {
                transports: ['websocket', 'polling'],
                autoConnect: true,
                reconnection: true,
                reconnectionAttempts: 5,
                reconnectionDelay: 1000,
                timeout: 20000,
                auth: {
                    token: token || ''
                },
                query: {
                    userId: userId,
                    platform: Platform.OS
                }
            });

            this.setupEventHandlers();

            return new Promise((resolve) => {
                const timeout = setTimeout(() => {
                    devLog('Socket', 'Connection timeout, resolving anyway');
                    resolve();
                }, 10000);

                this.socket.once('connect', () => {
                    clearTimeout(timeout);
                    devLog('Socket', 'Connected successfully');
                    this.connectionState = 'connected';

                    // Authenticate
                    if (userId) {
                        this.socket.emit('authenticate', { userId });
                    }

                    resolve();
                });

                this.socket.once('connect_error', (error) => {
                    clearTimeout(timeout);
                    devError('Socket', 'Connection error:', error.message);
                    this.connectionState = 'disconnected';
                    resolve(); // Resolve anyway to not block the app
                });
            });
        } catch (error) {
            devError('Socket', 'Connection setup failed:', error);
            return Promise.resolve();
        }
    }

    setupEventHandlers() {
        if (!this.socket) return;

        this.socket.on('connect', () => {
            devLog('Socket', 'Connected event');
            this.connectionState = 'connected';
            this.emit('connection_state_change', 'connected');
        });

        this.socket.on('disconnect', (reason) => {
            devLog('Socket', `Disconnected: ${reason}`);
            this.connectionState = 'disconnected';
            this.isAuthenticated = false;
            this.emit('connection_state_change', 'disconnected');
        });

        this.socket.on('authenticated', (data) => {
            devLog('Socket', 'Authenticated successfully');
            this.isAuthenticated = true;
            this.emit('authenticated', data);
        });

        this.socket.on('auth_error', (data) => {
            devError('Socket', 'Authentication error:', data);
            this.isAuthenticated = false;
        });

        // Debug: Log all events
        this.socket.onAny((eventName, ...args) => {
            devLog('Socket', `Event: ${eventName}`, args);
        });
    }

    emit(event, data) {
        if (this.socket?.connected) {
            this.socket.emit(event, data);
        } else {
            devLog('Socket', `Cannot emit ${event}, not connected`);
        }
    }

    on(event, callback) {
        if (!this.socket) {
            devLog('Socket', 'Socket not initialized');
            return;
        }

        this.socket.on(event, callback);

        // Track listeners
        if (!this.listeners.has(event)) {
            this.listeners.set(event, new Set());
        }
        this.listeners.get(event).add(callback);
    }

    off(event, callback) {
        if (!this.socket) return;

        this.socket.off(event, callback);

        if (this.listeners.has(event)) {
            this.listeners.get(event).delete(callback);
        }
    }

    joinRoom(roomId) {
        this.emit('joinRoom', roomId);
    }

    leaveRoom(roomId) {
        this.emit('leaveRoom', roomId);
    }

    typing(roomId, isTyping) {
        this.emit('typing', { roomId, isTyping });
    }

    disconnect() {
        if (this.socket) {
            this.socket.removeAllListeners();
            this.socket.disconnect();
            this.socket = null;
        }

        this.listeners.clear();
        this.isAuthenticated = false;
        this.connectionState = 'disconnected';
        this.userId = null;
    }

    isConnected() {
        return this.socket?.connected === true;
    }

    getSocketId() {
        return this.socket?.id || null;
    }

    getConnectionState() {
        return this.connectionState;
    }
}

