/**
 * App Stability Enhancements
 * Comprehensive improvements for API connection and app stability
 */

import NetInfo from '@react-native-community/netinfo';
import socketService from '../services/socketService';
import realtimeService from '../services/realtimeService';
import apiClient from '../services/api/client';
import { devLog, devError } from './devLog';

class AppStabilityManager {
    constructor() {
        this.isOnline = true;
        this.connectionQuality = 'good';
        this.apiHealth = { success: 0, failure: 0, latency: [] };
        this.socketReconnectAttempts = 0;
        this.maxSocketReconnectAttempts = 10;
        this.healthCheckInterval = null;
        this.networkListener = null;
    }

    /**
     * Initialize stability monitoring
     */
    initialize() {
        devLog('AppStability', 'Initializing stability monitoring');

        // Monitor network connectivity
        this.setupNetworkMonitoring();

        // Monitor API health
        this.startHealthChecks();

        // Setup error boundaries
        this.setupGlobalErrorHandling();

        // Monitor socket stability
        this.monitorSocketConnection();

        // Setup performance monitoring
        this.setupPerformanceMonitoring();
    }

    /**
     * Network monitoring
     */
    setupNetworkMonitoring() {
        this.networkListener = NetInfo.addEventListener(state => {
            const wasOnline = this.isOnline;
            this.isOnline = state.isConnected && state.isInternetReachable;

            devLog('AppStability', 'Network state changed:', {
                isOnline: this.isOnline,
                type: state.type,
                details: state.details
            });

            // Handle connection changes
            if (!wasOnline && this.isOnline) {
                devLog('AppStability', 'Connection restored, syncing data...');
                this.onConnectionRestored();
            } else if (wasOnline && !this.isOnline) {
                devLog('AppStability', 'Connection lost');
                this.onConnectionLost();
            }

            // Update connection quality
            this.updateConnectionQuality(state);
        });
    }

    /**
     * Update connection quality based on network state
     */
    updateConnectionQuality(state) {
        if (!state.isConnected) {
            this.connectionQuality = 'offline';
        } else if (state.type === 'wifi') {
            this.connectionQuality = 'good';
        } else if (state.type === 'cellular') {
            const effectiveType = state.details?.cellularGeneration;
            if (effectiveType === '4g' || effectiveType === '5g') {
                this.connectionQuality = 'good';
            } else if (effectiveType === '3g') {
                this.connectionQuality = 'fair';
            } else {
                this.connectionQuality = 'poor';
            }
        } else {
            this.connectionQuality = 'unknown';
        }

        devLog('AppStability', `Connection quality: ${this.connectionQuality}`);
    }

    /**
     * Handle connection restored
     */
    async onConnectionRestored() {
        // Clear all API cache to get fresh data
        apiClient.clearAllCache();

        // Reconnect socket if needed
        if (!socketService.isConnected()) {
            this.socketReconnectAttempts = 0;
            await this.reconnectSocket();
        }

        // Trigger data refresh in screens
        this.emit('connection_restored');
    }

    /**
     * Handle connection lost
     */
    onConnectionLost() {
        // Notify screens about offline state
        this.emit('connection_lost');
    }

    /**
     * Reconnect socket with exponential backoff
     */
    async reconnectSocket() {
        if (this.socketReconnectAttempts >= this.maxSocketReconnectAttempts) {
            devError('AppStability', 'Max socket reconnection attempts reached');
            return;
        }

        this.socketReconnectAttempts++;
        const delay = Math.min(1000 * Math.pow(2, this.socketReconnectAttempts - 1), 30000);

        devLog('AppStability', `Attempting socket reconnection (${this.socketReconnectAttempts}/${this.maxSocketReconnectAttempts}) in ${delay}ms`);

        setTimeout(async () => {
            if (this.isOnline && !socketService.isConnected()) {
                try {
                    await socketService.forceReconnect();
                    this.socketReconnectAttempts = 0;
                    devLog('AppStability', 'Socket reconnected successfully');
                } catch (error) {
                    devError('AppStability', 'Socket reconnection failed:', error);
                    this.reconnectSocket(); // Retry
                }
            }
        }, delay);
    }

    /**
     * Monitor socket connection stability
     */
    monitorSocketConnection() {
        // Check socket health every 30 seconds
        setInterval(() => {
            const realtimeStatus = realtimeService.getStatus();

            if (realtimeStatus.mode === 'none' && this.isOnline) {
                devLog('AppStability', 'Real-time connection lost, attempting recovery...');
                this.reconnectSocket();
            }

            // Log connection mode for debugging
            if (__DEV__) {
                devLog('AppStability', 'Real-time status:', realtimeStatus);
            }
        }, 30000);
    }

    /**
     * Start API health checks
     */
    startHealthChecks() {
        // Initial health check
        this.checkAPIHealth();

        // Regular health checks every minute
        this.healthCheckInterval = setInterval(() => {
            this.checkAPIHealth();
        }, 60000);
    }

    /**
     * Check API health
     */
    async checkAPIHealth() {
        const start = Date.now();

        try {
            const response = await fetch(`${apiClient.defaults.baseURL}/health`, {
                method: 'GET',
                timeout: 5000
            });

            const latency = Date.now() - start;
            this.apiHealth.latency.push(latency);

            if (this.apiHealth.latency.length > 10) {
                this.apiHealth.latency.shift(); // Keep last 10 measurements
            }

            if (response.ok) {
                this.apiHealth.success++;
                devLog('AppStability', `API health check passed (${latency}ms)`);
            } else {
                this.apiHealth.failure++;
                devError('AppStability', `API health check failed: ${response.status}`);
            }
        } catch (error) {
            this.apiHealth.failure++;
            devError('AppStability', 'API health check error:', error.message);
        }

        // Calculate average latency
        const avgLatency = this.apiHealth.latency.reduce((a, b) => a + b, 0) / this.apiHealth.latency.length;

        // Warn if API is slow or unreliable
        if (avgLatency > 3000) {
            devLog('AppStability', `âš ï¸ API is slow (avg ${Math.round(avgLatency)}ms)`);
        }

        const successRate = this.apiHealth.success / (this.apiHealth.success + this.apiHealth.failure);
        if (successRate < 0.9) {
            devLog('AppStability', `âš ï¸ API reliability issues (${Math.round(successRate * 100)}% success rate)`);
        }
    }

    /**
     * Setup global error handling
     */
    setupGlobalErrorHandling() {
        // Handle unhandled promise rejections
        const originalHandler = global.onunhandledrejection;
        global.onunhandledrejection = (event) => {
            devError('AppStability', 'Unhandled promise rejection:', event);

            // Check if it's a network error
            if (event.reason?.message?.includes('Network') || 
                event.reason?.message?.includes('fetch')) {
                this.handleNetworkError(event.reason);
            }

            // Call original handler if exists
            if (originalHandler) {
                originalHandler(event);
            }
        };
    }

    /**
     * Handle network errors gracefully
     */
    handleNetworkError(error) {
        devLog('AppStability', 'Handling network error:', error.message);

        // Don't spam the user with alerts
        if (!this.lastNetworkErrorTime || Date.now() - this.lastNetworkErrorTime > 10000) {
            this.lastNetworkErrorTime = Date.now();
            this.emit('network_error', error);
        }
    }

    /**
     * Setup performance monitoring
     */
    setupPerformanceMonitoring() {
        // Monitor JS frame rate
        let lastFrameTime = Date.now();
        let frameCount = 0;

        const measureFrameRate = () => {
            frameCount++;
            const now = Date.now();

            if (now - lastFrameTime >= 1000) {
                const fps = frameCount;
                frameCount = 0;
                lastFrameTime = now;

                if (fps < 30) {
                    devLog('AppStability', `âš ï¸ Low frame rate: ${fps} FPS`);
                }
            }

            requestAnimationFrame(measureFrameRate);
        };

        if (__DEV__) {
            requestAnimationFrame(measureFrameRate);
        }
    }

    /**
     * Get stability status
     */
    getStatus() {
        const avgLatency = this.apiHealth.latency.reduce((a, b) => a + b, 0) / (this.apiHealth.latency.length || 1);
        const successRate = this.apiHealth.success / (this.apiHealth.success + this.apiHealth.failure || 1);

        return {
            isOnline: this.isOnline,
            connectionQuality: this.connectionQuality,
            apiHealth: {
                avgLatency: Math.round(avgLatency),
                successRate: Math.round(successRate * 100),
                totalRequests: this.apiHealth.success + this.apiHealth.failure
            },
            socketStatus: socketService.isConnected() ? 'connected' : 'disconnected',
            realtimeMode: realtimeService.getStatus().mode
        };
    }

    /**
     * Event emitter functionality
     */
    listeners = new Map();

    on(event, callback) {
        if (!this.listeners.has(event)) {
            this.listeners.set(event, new Set());
        }
        this.listeners.get(event).add(callback);

        return () => {
            this.listeners.get(event)?.delete(callback);
        };
    }

    emit(event, data) {
        this.listeners.get(event)?.forEach(callback => {
            try {
                callback(data);
            } catch (error) {
                devError('AppStability', `Error in ${event} listener:`, error);
            }
        });
    }

    /**
     * Cleanup
     */
    cleanup() {
        if (this.networkListener) {
            this.networkListener();
        }
        if (this.healthCheckInterval) {
            clearInterval(this.healthCheckInterval);
        }
        this.listeners.clear();
    }
}

// Create singleton instance
const appStabilityManager = new AppStabilityManager();

// Export for debugging
if (__DEV__) {
    global.appStabilityManager = appStabilityManager;
}

export default appStabilityManager;

// frontend/src/utils/cacheManager.js
import AsyncStorage from '@react-native-async-storage/async-storage';

class CacheManager {
    constructor() {
        this.memoryCache = new Map();
        this.cacheTimestamps = new Map();
        this.defaultTTL = 5 * 60 * 1000; // 5 minutes
        this.maxMemoryCacheSize = 50; // Maximum items in memory
        this.persistentKeys = new Set(); // Keys that should persist across sessions

        // Start periodic cleanup
        this.startCleanupTimer();
    }

    // Set cache with TTL
    async set(key, value, options = {}) {
        const { ttl = this.defaultTTL, persistent = false, skipMemory = false } = options;

        const cacheData = {
            value,
            timestamp: Date.now(),
            ttl,
        };

        // Store in memory cache if not skipped
        if (!skipMemory) {
            this.setMemoryCache(key, cacheData);
        }

        // Store in persistent storage if needed
        if (persistent) {
            this.persistentKeys.add(key);
            try {
                await AsyncStorage.setItem(`cache_${key}`, JSON.stringify(cacheData));
            } catch (error) {
                console.error('Failed to persist cache:', error);
            }
        }

        return value;
    }

    // Get from cache
    async get(key, options = {}) {
        const { fallback = null, forceRefresh = false } = options;

        if (forceRefresh) {
            this.invalidate(key);
            return fallback;
        }

        // Check memory cache first
        const memoryData = this.getFromMemoryCache(key);
        if (memoryData !== null) {
            return memoryData;
        }

        // Check persistent storage
        if (this.persistentKeys.has(key)) {
            try {
                const stored = await AsyncStorage.getItem(`cache_${key}`);
                if (stored) {
                    const cacheData = JSON.parse(stored);
                    if (this.isValid(cacheData)) {
                        // Restore to memory cache
                        this.setMemoryCache(key, cacheData);
                        return cacheData.value;
                    } else {
                        // Clean up expired data
                        await AsyncStorage.removeItem(`cache_${key}`);
                        this.persistentKeys.delete(key);
                    }
                }
            } catch (error) {
                console.error('Failed to read from persistent cache:', error);
            }
        }

        return fallback;
    }

    // Get or set pattern
    async getOrSet(key, fetcher, options = {}) {
        const cached = await this.get(key, { forceRefresh: options.forceRefresh });

        if (cached !== null && cached !== undefined) {
            return cached;
        }

        try {
            const value = await fetcher();
            return await this.set(key, value, options);
        } catch (error) {
            console.error('Failed to fetch and cache:', error);
            throw error;
        }
    }

    // Set memory cache with size limit
    setMemoryCache(key, cacheData) {
        // Remove oldest entries if cache is too large
        if (this.memoryCache.size >= this.maxMemoryCacheSize) {
            const oldestKey = this.getOldestCacheKey();
            if (oldestKey) {
                this.memoryCache.delete(oldestKey);
                this.cacheTimestamps.delete(oldestKey);
            }
        }

        this.memoryCache.set(key, cacheData);
        this.cacheTimestamps.set(key, cacheData.timestamp);
    }

    // Get from memory cache
    getFromMemoryCache(key) {
        const cacheData = this.memoryCache.get(key);

        if (!cacheData) {
            return null;
        }

        if (this.isValid(cacheData)) {
            return cacheData.value;
        }

        // Clean up expired data
        this.memoryCache.delete(key);
        this.cacheTimestamps.delete(key);
        return null;
    }

    // Check if cache data is valid
    isValid(cacheData) {
        const { timestamp, ttl } = cacheData;
        return Date.now() - timestamp < ttl;
    }

    // Get oldest cache key
    getOldestCacheKey() {
        let oldestKey = null;
        let oldestTime = Infinity;

        this.cacheTimestamps.forEach((timestamp, key) => {
            if (timestamp < oldestTime) {
                oldestTime = timestamp;
                oldestKey = key;
            }
        });

        return oldestKey;
    }

    // Invalidate specific key
    async invalidate(key) {
        this.memoryCache.delete(key);
        this.cacheTimestamps.delete(key);

        if (this.persistentKeys.has(key)) {
            this.persistentKeys.delete(key);
            try {
                await AsyncStorage.removeItem(`cache_${key}`);
            } catch (error) {
                console.error('Failed to remove persistent cache:', error);
            }
        }
    }

    // Invalidate keys matching pattern
    async invalidatePattern(pattern) {
        const regex = new RegExp(pattern);
        const keysToInvalidate = [];

        this.memoryCache.forEach((_, key) => {
            if (regex.test(key)) {
                keysToInvalidate.push(key);
            }
        });

        await Promise.all(keysToInvalidate.map((key) => this.invalidate(key)));
    }

    // Clear all cache
    async clear() {
        this.memoryCache.clear();
        this.cacheTimestamps.clear();

        // Clear persistent cache
        try {
            const keys = await AsyncStorage.getAllKeys();
            const cacheKeys = keys.filter((key) => key.startsWith('cache_'));
            await AsyncStorage.multiRemove(cacheKeys);
            this.persistentKeys.clear();
        } catch (error) {
            console.error('Failed to clear persistent cache:', error);
        }
    }

    // Get cache statistics
    getStats() {
        let validCount = 0;
        let expiredCount = 0;
        let totalSize = 0;

        this.memoryCache.forEach((cacheData) => {
            if (this.isValid(cacheData)) {
                validCount++;
            } else {
                expiredCount++;
            }

            // Rough size estimation
            totalSize += JSON.stringify(cacheData.value).length;
        });

        return {
            totalEntries: this.memoryCache.size,
            validEntries: validCount,
            expiredEntries: expiredCount,
            persistentEntries: this.persistentKeys.size,
            estimatedSize: totalSize,
        };
    }

    // Periodic cleanup of expired entries
    startCleanupTimer() {
        setInterval(() => {
            this.cleanup();
        }, 60 * 1000); // Run every minute
    }

    // Clean up expired entries
    cleanup() {
        const keysToDelete = [];

        this.memoryCache.forEach((cacheData, key) => {
            if (!this.isValid(cacheData)) {
                keysToDelete.push(key);
            }
        });

        keysToDelete.forEach((key) => {
            this.memoryCache.delete(key);
            this.cacheTimestamps.delete(key);
        });

        if (keysToDelete.length > 0) {
            console.log(`Cleaned up ${keysToDelete.length} expired cache entries`);
        }
    }

    // Preload cache for critical data
    async preload(entries) {
        const promises = entries.map(({ key, fetcher, options }) =>
            this.getOrSet(key, fetcher, { ...options, forceRefresh: true }),
        );

        try {
            await Promise.all(promises);
            console.log(`Preloaded ${entries.length} cache entries`);
        } catch (error) {
            console.error('Failed to preload cache:', error);
        }
    }
}

// Create singleton instance
const cacheManager = new CacheManager();

// Export convenience functions
export const cache = {
    get: (key, options) => cacheManager.get(key, options),
    set: (key, value, options) => cacheManager.set(key, value, options),
    getOrSet: (key, fetcher, options) => cacheManager.getOrSet(key, fetcher, options),
    invalidate: (key) => cacheManager.invalidate(key),
    invalidatePattern: (pattern) => cacheManager.invalidatePattern(pattern),
    clear: () => cacheManager.clear(),
    getStats: () => cacheManager.getStats(),
    preload: (entries) => cacheManager.preload(entries),
};

export default cacheManager;

/**
 * Chat Socket Fix - Ensures proper socket connection for real-time chat
 */

import socketService from '../services/socketService';
import { devLog, devError } from './devLog';

export const ensureChatSocketConnection = async (userId, roomId) => {
    devLog('ChatSocketFix', 'Ensuring socket connection...', { userId, roomId });

    try {
        // 1. Check if socket is connected
        if (!socketService.socket?.connected) {
            devLog('ChatSocketFix', 'Socket not connected, attempting connection...');
            await socketService.connect(userId);

            // Wait for connection
            await new Promise((resolve) => {
                const checkConnection = setInterval(() => {
                    if (socketService.socket?.connected) {
                        clearInterval(checkConnection);
                        resolve();
                    }
                }, 100);

                // Timeout after 5 seconds
                setTimeout(() => {
                    clearInterval(checkConnection);
                    resolve();
                }, 5000);
            });
        }

        // 2. Check if authenticated
        if (!socketService.isAuthenticated) {
            devLog('ChatSocketFix', 'Socket not authenticated, waiting for auth...');

            // Wait for authentication
            await new Promise((resolve) => {
                const checkAuth = setInterval(() => {
                    if (socketService.isAuthenticated) {
                        clearInterval(checkAuth);
                        resolve();
                    }
                }, 100);

                // Timeout after 3 seconds
                setTimeout(() => {
                    clearInterval(checkAuth);
                    resolve();
                }, 3000);
            });
        }

        // 3. Join room if authenticated
        if (socketService.socket?.connected && socketService.isAuthenticated) {
            devLog('ChatSocketFix', `ðŸšª Joining room: ${roomId}`);

            // Use direct socket emit to ensure room join
            socketService.socket.emit('joinRoom', roomId);

            // Also join the room with room_ prefix to match backend
