    };
    socket.onAny(anyHandler);

    // Return cleanup function
    return () => {
        Object.entries(listeners).forEach(([event, handler]) => {
            socket.off(event, handler);
        });
        socket.offAny(anyHandler);
    };
};

// frontend/src/utils/debounce.js

/**
 * Creates a debounced function that delays invoking func until after wait milliseconds
 * have elapsed since the last time the debounced function was invoked.
 *
 * @param {Function} func - The function to debounce
 * @param {number} wait - The number of milliseconds to delay
 * @param {boolean} immediate - If true, trigger the function on the leading edge instead of the trailing
 * @returns {Function} The debounced function
 */
export function debounce(func, wait, immediate = false) {
    let timeout;

    return function executedFunction(...args) {
        const later = () => {
            timeout = null;
            if (!immediate) func(...args);
        };

        const callNow = immediate && !timeout;

        clearTimeout(timeout);
        timeout = setTimeout(later, wait);

        if (callNow) func(...args);
    };
}

/**
 * Creates a throttled function that only invokes func at most once per every wait milliseconds.
 *
 * @param {Function} func - The function to throttle
 * @param {number} wait - The number of milliseconds to throttle invocations to
 * @returns {Function} The throttled function
 */
export function throttle(func, wait) {
    let inThrottle;
    let lastTime = 0;

    return function (...args) {
        const now = Date.now();

        if (!inThrottle && now - lastTime >= wait) {
            func.apply(this, args);
            lastTime = now;
            inThrottle = true;

            setTimeout(() => {
                inThrottle = false;
            }, wait);
        }
    };
}

export default { debounce, throttle };

