/**
 * Test direct socket connection without authentication
 */

import io from 'socket.io-client';
import { API_BASE_URL } from '../config/network';
import { devLog, devError } from './devLog';
import AsyncStorage from '@react-native-async-storage/async-storage';

export const testDirectSocketConnection = async () => {
    devLog('TestSocket', '=== Starting Direct Socket Connection Test ===');

    try {
        // Get stored token
        const token = await AsyncStorage.getItem('userToken');
        const userStr = await AsyncStorage.getItem('user');
        const user = userStr ? JSON.parse(userStr) : null;

        devLog('TestSocket', 'Auth info:', {
            hasToken: !!token,
            hasUser: !!user,
            userId: user?._id
        });

        // Get socket URL
        const socketUrl = API_BASE_URL.replace('/api', '');
        devLog('TestSocket', 'Connecting to:', socketUrl);

        // Create test socket with minimal config
        const testSocket = io(socketUrl, {
            transports: ['polling', 'websocket'],
            path: '/socket.io/',
            autoConnect: false,
            timeout: 10000,
            reconnection: false,
            query: {
                userId: user?._id || 'test-user',
                platform: 'test',
                token: token || ''
            },
            auth: {
                token: token || ''
            }
        });

        // Setup event handlers
        testSocket.on('connect', () => {
            devLog('TestSocket', 'âœ… Connected!', {
                id: testSocket.id,
                connected: testSocket.connected
            });
        });

        testSocket.on('connect_error', (error) => {
            devError('TestSocket', 'âŒ Connection error:', {
                message: error.message,
                type: error.type,
                data: error.data
            });
        });

        testSocket.on('disconnect', (reason) => {
            devLog('TestSocket', 'Disconnected:', reason);
        });

        testSocket.onAny((eventName, ...args) => {
            devLog('TestSocket', `Event received: ${eventName}`, args);
        });

        // Connect
        devLog('TestSocket', 'Attempting connection...');
        testSocket.connect();

        // Wait for result
        await new Promise((resolve) => {
            setTimeout(() => {
                const result = {
                    connected: testSocket.connected,
                    id: testSocket.id,
                    disconnected: testSocket.disconnected
                };
                devLog('TestSocket', 'Final status:', result);
                testSocket.disconnect();
                resolve(result);
            }, 5000);
        });

    } catch (error) {
        devError('TestSocket', 'Test failed:', error);
    }

    devLog('TestSocket', '=== Test Complete ===');
};

// Also test basic HTTP connection
export const testHttpConnection = async () => {
    try {
        const response = await fetch(`${API_BASE_URL}/health`, {
            method: 'GET',
            headers: {
                'Accept': 'application/json'
            }
        });
        const data = await response.json();
        devLog('TestSocket', 'HTTP health check:', {
            status: response.status,
            ok: response.ok,
            data
        });
        return response.ok;
    } catch (error) {
        devError('TestSocket', 'HTTP test failed:', error);
        return false;
    }
};

/**
 * Test Socket Connection - Debug tool to test real-time message reception
 */

import socketService from '../services/socketService';
import chatService from '../services/chatService';
import { devLog } from './devLog';

export class SocketConnectionTester {
    constructor() {
        this.receivedEvents = [];
        this.receivedMessages = [];
    }

    async runTest(userId, roomId) {
        devLog('SocketTest', 'ðŸ§ª Starting socket connection test', { userId, roomId });

        try {
            // Step 1: Connect socket
            devLog('SocketTest', '1ï¸âƒ£ Connecting socket...');
            await socketService.connect(userId);

            // Wait for connection
            await this.waitForConnection();

            // Step 2: Check authentication
            devLog('SocketTest', '2ï¸âƒ£ Checking authentication...');
            if (!socketService.isAuthenticated) {
                devLog('SocketTest', 'â³ Waiting for authentication...');
                await this.waitForAuthentication();
            }

            devLog('SocketTest', 'âœ… Socket authenticated');

            // Step 3: Join room
            devLog('SocketTest', '3ï¸âƒ£ Joining room...');
            socketService.joinRoom(roomId);

            // Step 4: Setup direct listeners
            devLog('SocketTest', '4ï¸âƒ£ Setting up test listeners...');
            this.setupTestListeners(roomId);

            // Step 5: Send a test message
            devLog('SocketTest', '5ï¸âƒ£ Sending test message...');
            const testContent = `Test message from socket tester at ${new Date().toLocaleTimeString()}`;

            try {
                const sentMessage = await chatService.sendMessage(roomId, testContent);
                devLog('SocketTest', 'âœ… Test message sent', sentMessage);
            } catch (error) {
                devLog('SocketTest', 'âŒ Failed to send test message', error);
            }

            // Step 6: Wait and check for received events
            devLog('SocketTest', '6ï¸âƒ£ Waiting for socket events...');
            await new Promise(resolve => setTimeout(resolve, 5000));

            // Generate report
            this.generateReport();

        } catch (error) {
            devLog('SocketTest', 'âŒ Test failed', error);
        }
    }

    async waitForConnection() {
        const maxWait = 5000;
        const checkInterval = 100;
        let waited = 0;

        while (!socketService.socket?.connected && waited < maxWait) {
            await new Promise(resolve => setTimeout(resolve, checkInterval));
            waited += checkInterval;
        }

        if (!socketService.socket?.connected) {
            throw new Error('Socket connection timeout');
        }

        devLog('SocketTest', 'âœ… Socket connected');
    }

    async waitForAuthentication() {
        const maxWait = 3000;
        const checkInterval = 100;
        let waited = 0;

        while (!socketService.isAuthenticated && waited < maxWait) {
            await new Promise(resolve => setTimeout(resolve, checkInterval));
            waited += checkInterval;
        }

        if (!socketService.isAuthenticated) {
            throw new Error('Authentication timeout');
        }
    }

    setupTestListeners(roomId) {
        const socket = socketService.socket;
        if (!socket) {
            devLog('SocketTest', 'âŒ No socket available');
            return;
        }

        // Listen to all events
        socket.onAny((eventName, ...args) => {
            const event = {
                eventName,
                args,
                timestamp: new Date().toISOString()
            };

            this.receivedEvents.push(event);
            devLog('SocketTest', `ðŸ“¨ Event received: ${eventName}`, args);

            // Track message events specifically
            if (eventName === 'new_message') {
                this.receivedMessages.push(event);
                devLog('SocketTest', 'ðŸ”´ NEW MESSAGE EVENT!', event);
            }
        });

        // Also add specific new_message listener
        socket.on('new_message', (data) => {
            devLog('SocketTest', 'ðŸŽ¯ Direct new_message listener triggered!', data);
        });

        devLog('SocketTest', 'âœ… Test listeners setup complete');
    }

    generateReport() {
        const report = {
            socketState: {
                connected: socketService.socket?.connected,
                authenticated: socketService.isAuthenticated,
                socketId: socketService.socket?.id
            },
            eventsReceived: this.receivedEvents.length,
            messageEventsReceived: this.receivedMessages.length,
            allEvents: this.receivedEvents,
            messageEvents: this.receivedMessages
        };

        devLog('SocketTest', 'ðŸ“Š Test Report:', report);

        if (this.receivedMessages.length === 0) {
            devLog('SocketTest', 'âš ï¸ No message events received!');
            devLog('SocketTest', 'Possible issues:');
            devLog('SocketTest', '1. Backend not emitting to correct room');
            devLog('SocketTest', '2. Socket not properly joined to room');
            devLog('SocketTest', '3. Event name mismatch');
            devLog('SocketTest', '4. Authentication issues');
        } else {
            devLog('SocketTest', 'âœ… Message events received successfully!');
        }

        return report;
    }
}

// Export for easy testing
export const testSocketConnection = async (userId, roomId) => {
    const tester = new SocketConnectionTester();
    await tester.runTest(userId, roomId);
};

// Attach to window for development
if (__DEV__ && typeof window !== 'undefined') {
    window.testSocketConnection = testSocketConnection;
}
